//starting my week on sunday

let day=2
switch(day){
    case 1:
    console.log("sunday")
    break;
    case 2:
        console.log("monday")
    break;
    case 3:
        console.log("tuesday")
    break ;
    default :
    console.log("monday")
}  
 
